function ChatClient() {}
ChatClient.prototype.websocket = null;
ChatClient.prototype.username = null;

ChatClient.prototype.connect = function(Username_, Serveraddress_)
{
  if(!this.websocket)
  {
    this.websocket = new WebSocket(Serveraddress_);
    this.websocket.username = Username_;

    this.websocket.onerror = this.on_error;
    this.websocket.onopen = this.on_open;
    this.websocket.onmessage = this.on_message;
    this.websocket.onclose = this.on_close;
  }
}

ChatClient.prototype.on_error = function(Event_)
{
  log(Event_.data);
}

