var Client = new ChatClient();

function log( Message_ )
{
  $('#ChatLog').prepend( Message_ + "\n------------------------------\n" );
}

$(document).ready(function()
{
  $('#WebChatLoginForm').on('submit', function(e)
  {
    e.preventDefault();

    Client.connect($('#Username').val(), $('#Serveraddress').val());

  });

  $(function()
  {
    $('#ChatLoginBody').dialog({show: "slide", modal: true, autoOpen: true, closeOnEscape: false });
  });
});